s:Application.fx:Script:

	private function init():void{
		//It has begun
		fzbzTxt.text="";
		
		for (var i:int=1; i <= 100; i++){
			
			var isFizz:Boolean =isFizz(i);//, isStage2);
			var isBuzz:Boolean =isBuzz(i);//, isStage2);
			
			if (isFizz && isBuzz)
				fzbzTxt.text+="FizzBuzz\n";
			else if (isFizz)
				fzbzTxt.text+="Fizz\n";
			else if (isBuzz)
				fzbzTxt.text+="Buzz\n";
			else //(!isFizz && !isBuzz)
				fzbzTxt.text+=i+"\n";
		}
	}
	private function isFizz(i:int):Boolean{
		//stage 2
		//var s:String = String(i);
		if (i % 3 == 0 ) // || (isStage2 && s.contains("3")))
			return true;
		return false;
	}
	private function isBuzz(i:int):Boolean{
		//stage 2
		//var s:String = String(i);
		if (i % 5 == 0 ) // || (isStage2 && s.contains("5")))
			return true;
		return false;
	}
