/*

DROP TABLE #tmp

SELECT job_id,name
--INTO #tmp
FROM msdb.dbo.sysjobs
WHERE enabled = 0
ORDER BY name

DELETE FROM #tmp
WHERE name IN ( 
'Email Ancestry Daily',
'ZZZ_CrawlGroupSlidingWindow',
'(Black Friday) GenerateBidRank Info us retail client only',
'(BlackFriday) SI_SP_CSKFutureBid_Process',
'_AdSweep(Remove after Completion)',
'Admin_Gather Index Fragmentation Stats',
'Admin_Rebuild Indexes'

)


SELECT * FROM #tmp


*/
DECLARE @job_id UNIQUEIDENTIFIER,
	@enable BIT = 1

WHILE EXISTS(SELECT TOP 1 1 FROM @Jobs WHERE Complete = 0) 
BEGIN

	SELECT TOP 1 @job_id = job_id 
	FROM @Jobs 
	WHERE Complete = 0

     EXEC msdb.dbo.sp_update_job @job_id = @job_id, @enabled = @enable
     
     UPDATE @Jobs
     SET Complete = 1
     WHERE Job_ID = @job_id


END
